public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int N = sc.nextInt();
        long count = 0;

        for (int A = 1; A < N; A++) {
            int maxB = (N - 1) / A;
            count += maxB;
        }

        System.out.println(count);
    }
}

