import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long X = sc.nextLong();
        long balance = 100;
        int years = 0;

        while (balance < X) {
            balance += balance / 100;
            years++;
        }

        System.out.println(years);
    }
}

