import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int N = sc.nextInt();
        int R = sc.nextInt();

        int innerRating;
        if (N >= 10) {
            innerRating = R;
        } else {
            innerRating = R + 100 * (10 - N);
        }

        System.out.println(innerRating);
    }
}

