import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int N = sc.nextInt();
        String S = sc.next();
        String T = sc.next();
        StringBuilder sb = new StringBuilder();

        for (int i = 0; i < N; i++) {
            sb.append(S.charAt(i));
            sb.append(T.charAt(i));
        }

        System.out.println(sb.toString());
    }
}

