import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String S = sc.next();
        int n = S.length();
        int hugs = 0;

        for (int i = 0; i < n / 2; i++) {
            if (S.charAt(i) != S.charAt(n - 1 - i)) {
                hugs++;
            }
        }

        System.out.println(hugs);
    }
}

