import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int N = sc.nextInt();
        boolean found = false;

        for (int X = 1; X <= N; X++) {
            if ((int)(X * 1.08) == N) {
                System.out.println(X);
                found = true;
                break;
            }
        }

        if (!found) {
            System.out.println(":(");
        }
    }
}

