import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int P = sc.nextInt();
        int Q = sc.nextInt();
        int R = sc.nextInt();

        int minSum = Math.min(P + Q, Math.min(Q + R, R + P));
        System.out.println(minSum);
    }
}

